mapper.select:
SELECT b.stdnt_no , nvl(sugang_lec_cnt, 0) sugang_lec_cnt , nvl(sugang_at_pnt, 0) sugang_at_pnt , b.name stdnt_name , b.grade stdnt_grade , c.sub_name , lec_able_pnt FROM ( SELECT a.stdnt_no, COUNT(a.lec_code) sugang_lec_cnt, SUM(c.lec_pnt) sugang_at_pnt FROM sugang_req a INNER JOIN lecture b ON (a.lec_code = b.lec_code) INNER JOIN curriculum c ON (b.cur_code = c.cur_code)
WHERE a.req_year = EXTRACT(YEAR FROM SYSDATE) AND a.req_semstr = (SELECT semstr FROM v_semstr)
AND a.sugang_at = #{sugang_at, jdbcType=VARCHAR} GROUP BY a.stdnt_no ) a RIGHT OUTER JOIN student b ON (a.stdnt_no = b.stdnt_no) LEFT OUTER JOIN subject c ON (b.sub_code = c.sub_code) LEFT OUTER JOIN v_stdnt_past_scre d ON (b.stdnt_no = d.stdnt_no) WHERE b.stdnt_no = #{stdnt_no, jdbcType=VARCHAR}

mapper.delete:
    	DELETE FROM sugang_req
			  WHERE lec_code = #{lec_code, jdbcType=VARCHAR}
			    AND stdnt_no = #{stdnt_no, jdbcType=VARCHAR}
