SELECT WEBMAIL_SEQ.NEXTVAL FROM DUAL
INSERT INTO webmail ( send_no, title, cont, read_at, sender, send_date, send_stat, delete_at
,atch_file_no
) VALUES ( #{send_no}, #{title, jdbcType=VARCHAR}, #{cont, jdbcType=VARCHAR}, 'N', #{sender, jdbcType=VARCHAR}, SYSDATE, 'Y', 'N'
,(SELECT MAX(atch_file_no) + 1 FROM attach)
)


	SELECT COUNT(*)+1 startAttNo
	FROM ATTACH
	WHERE atch_file_no = (SELECT MAX(atch_file_no) + 1 FROM attach)

	INSERT ALL

	INTO ATTACH(
		atch_file_no,
	    atch_file_seq,
	    file_path,
	    file_nm,
	    file_cont_type,
	    file_size,
	    reg_date,
	    reg_user_id,
	    biz_type,
	    save_file_nm
    )
	VALUES (
		(SELECT MAX(atch_file_no) + 1 FROM attach)
		, #{startAttNo} + #{idx}
		, #{attatch.file_path,jdbcType=VARCHAR}
		, #{attatch.file_nm,jdbcType=VARCHAR}
		, #{attatch.file_cont_type,jdbcType=VARCHAR}
		, #{attatch.file_size,jdbcType=NUMERIC}
		, sysdate
		, #{sender, jdbcType=VARCHAR}
		, 'WM'
		, #{attatch.save_file_nm,jdbcType=VARCHAR}
	)

	SELECT * FROM DUAL
